sys_script_client.script:
var hideMsgTimeout; // Déclarer une variable globale pour stocker le délai de masquage du message

function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '' || oldValue === newValue) {
      return;
   }

   // Afficher le message en haut du formulaire
   g_form.addInfoMessage(g_user.getFullName() + " don't forget to save");

   // Masquer le message après un court délai
   clearTimeout(hideMsgTimeout); // Effacer le délai précédent (s'il existe)
   hideMsgTimeout = setTimeout(function() {
      g_form.clearMessages();
   }, 3000); // Délai de 3 secondes (ajustable selon vos préférences)
}
